import { useState } from 'react'

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredEmail, setEnteredEmail] = useState('');
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const pattern = /^[^ ]+@[^ ]+\.[a-z]{2,3}$/;
  const enteredNameIsValid = enteredName.trim() !== '';
  const enteredEmailIsValid = enteredEmail.trim() !== '' && enteredEmail.match(pattern);
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;
  const emailInputIsInvalid = !enteredEmailIsValid && enteredNameTouched;

  let formIsValid = false;

  if(enteredNameIsValid) {
    formIsValid = true;
  }


  const nameInputChangeHandler = (e) => {
    setEnteredName(e.target.value);
  }

  const emailInputChangeHandler = (e) => {
    setEnteredEmail(e.target.value);
  }

  const nameInputBlurHandler = (e) => {
    setEnteredNameTouched(true);
  }

  const emailInputBlurHandler = (e) => {
    setEnteredNameTouched(true);
  }

  const formSubmissionHandler = (e) => {
    e.preventDefault();

    setEnteredNameTouched(true);

    if(!enteredNameIsValid) {
      return;
    }

    if(!enteredEmailIsValid) {
      return;
    }

    console.log("Name - ", enteredName);
    console.log("State - ", enteredEmail);

    // below works but not the cleanest way to do it
    // nameInputRef.current.value = '';
    setEnteredName('');
    setEnteredEmail('');
    setEnteredNameTouched(false);
  }

  const nameInputClasses = nameInputIsInvalid && emailInputIsInvalid ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
          type='text'
          id='name'
        />
        {nameInputIsInvalid && <p className="error-text">Name must not be empty</p>}

        <label htmlFor='name'>Your Email</label>
        <input
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
          type='email'
          id='email'
        />
        {emailInputIsInvalid && <p className="error-text">Email must not be empty</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
