import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  const {
    value: enteredFirstName,
    isValid: firstNameIsValid,
    hasError: firstNameHasError,
    valueChangeHandler: firstNameChangeHandler,
    inputBlurHandler: firstNameBlurHandler,
    reset: resetFirstNameInput
  } = useInput(value => value.trim() !== '');

  const {
    value: enteredLastName,
    isValid: enteredLastNameIsValid,
    hasError: enteredLastNameHasError,
    valueChangeHandler: enteredLastNameChangeHandler,
    inputBlurHandler: enteredLastNameBlurHandler,
    reset: resetEnteredLastNameInput
  } = useInput(value => value.trim() !== '');

  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasError: enteredEmailHasError,
    valueChangeHandler: enteredEmailChangeHandler,
    inputBlurHandler: enteredEmailBlurHandler,
    reset: resetEnteredEmailInput
  } = useInput(value => value.match(/^[^ ]+@[^ ]+\.[a-z]{2,3}$/));

  const handleFormSubmit = (e) => {
    e.preventDefault();

    if(!firstNameIsValid) {
      return;
    }

    if(!enteredLastNameIsValid) {
      return;
    }

    if(!enteredEmailIsValid) {
      return;
    }

    resetFirstNameInput();
    resetEnteredLastNameInput();
    resetEnteredEmailInput();
  }

  let formIsValid = false;

  if (firstNameIsValid && enteredLastNameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  }

  const nameInputClasses = firstNameHasError && enteredLastNameHasError && enteredEmailHasError ? "form-control invalid" : "form-control";


  return (
    <form onSubmit={handleFormSubmit}>
      <div className='control-group'>
        <div className={nameInputClasses}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text'
            id='name'
            value={enteredFirstName}
            onChange={firstNameChangeHandler}
            onBlur={firstNameBlurHandler}
          />
          {firstNameHasError && <p>First Name must be valid</p>}
        </div>
        <div className={nameInputClasses}>
          <label htmlFor='name'>Last Name</label>
          <input
            type='text'
            id='name'
            value={enteredLastName}
            onChange={enteredLastNameChangeHandler}
            onBlur={enteredLastNameBlurHandler}
          />
          {enteredLastNameHasError && <p>First Name must be valid</p>}
        </div>
      </div>
      <div className={nameInputClasses}>
        <label htmlFor='name'>E-Mail Address</label>
        <input
          type='email'
          id='name'
          value={enteredEmail}
          onChange={enteredEmailChangeHandler}
          onBlur={enteredEmailBlurHandler}
        />
        {enteredEmailHasError && <p>First Name must be valid</p>}
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
