        setChangedText(val => !val);
    };
  return (
    <div>
      <h2>My My My!</h2>
      <p>What a beautiful day to go outside...</p><br />
      <p>{changedText ? '(❁´◡`❁)' : '(¬_¬ )'}</p>
      <Output>{changedText ? 'Changed' : 'Not Changed'}</Output><br /><br />
      <button onClick={changeTextHandler}>Change Reaction</button>
    </div>
  )
}

export default Greeting
